import React, { useState, useEffect } from 'react';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [isRecording, setIsRecording] = useState(false);
  const [audioChunks, setAudioChunks] = useState([]);

  useEffect(() => {
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      navigator.mediaDevices.getUserMedia({ audio: true })
        .then(stream => {
          const recorder = new MediaRecorder(stream);
          setMediaRecorder(recorder);

          recorder.ondataavailable = (event) => {
            setAudioChunks(prev => [...prev, event.data]);
          };

          recorder.onstop = () => {
            const audioBlob = new Blob(audioChunks, { type: 'audio/wav' });
            const audioUrl = URL.createObjectURL(audioBlob);
            const audioMessage = { text: 'Voice message', sender: 'user', audioUrl };

            setMessages(prevMessages => [...prevMessages, audioMessage]);

            // Simulate bot response
            setMessages(prevMessages => [...prevMessages, audioMessage, { text: 'Hello! How can I help you?', sender: 'bot' }]);

            setAudioChunks([]);
          };
        });
    }
  }, [audioChunks]);

  const startRecording = () => {
    if (mediaRecorder) {
      mediaRecorder.start();
      setIsRecording(true);
    }
  };

  const stopRecording = () => {
    if (mediaRecorder) {
      mediaRecorder.stop();
      setIsRecording(false);
    }
  };

  const handleSend = () => {
    if (input.trim()) {
      setMessages([...messages, { text: input, sender: 'user' }]);
      setInput('');
      // Add bot response logic here
      setMessages([...messages, { text: input, sender: 'user' }, { text: 'Hello! How can I help you?', sender: 'bot' }]);
    }
  };

  return (
    <div className="h-screen flex items-end justify-center">
      <div className="w-full max-w-3xl">
        <div className="flex flex-col h-screen">
          <div className="flex-1 p-4 overflow-y-auto">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`my-2 p-2 rounded-lg ${message.sender === 'user' ? 'bg-blue-500 text-white self-end' : 'bg-gray-200 text-black self-start'}`}
              >
                {message.text}
                {message.audioUrl && (
                  <audio controls src={message.audioUrl} className="mt-2">
                    Your browser does not support the audio element.
                  </audio>
                )}
              </div>
            ))}
          </div>

          {/* Bottom bar */}
          <div className="flex flex-col items-center bg-black text-white p-4">
            <div className="flex items-center w-full max-w-lg">
              <button
                onMouseDown={startRecording}
                onMouseUp={stopRecording}
                className={`bg-gray-800 p-2 rounded-l-full ${isRecording ? 'bg-red-500' : ''}`}
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 1v22m-7-6h14" />
                </svg>
              </button>
              <input
                type="text"
                placeholder="Send a message..."
                className="flex-grow p-2 bg-gray-900 text-white border-none focus:outline-none"
                value={input}
                onChange={(e) => setInput(e.target.value)}
              />
              <button onClick={handleSend} className="bg-gray-800 p-2 rounded-r-full">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M7 13l3 3L17 7" />
                </svg>
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
