import React from 'react';
import { useState } from 'react';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim()) {
      setMessages([...messages, { text: input, sender: 'user' }]);
      setInput('');
      // Add bot response logic here
      setMessages([...messages, { text: input, sender: 'user' }, { text: 'Hello! How can I help you?', sender: 'bot' }]);
    }
  };

  return (
    <div className="flex flex-col items-center bg-black text-white p-4">
    <div className="flex items-center w-full max-w-lg">
      <button className="bg-gray-800 p-2 rounded-l-full">
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 10h6m4 0h7m-7 4h7m0 0H3m0 0h6m4 0h7" />
        </svg>
      </button>
      <input
        type="text"
        placeholder="Send a message..."
        className="flex-grow p-2 bg-gray-900 text-white border-none focus:outline-none"
      />
      <button className="bg-gray-800 p-2 rounded-r-full">
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M7 13l3 3L17 7" />
        </svg>
      </button>
    </div>
    <div className="flex justify-center mt-2">
      <span className="text-gray-400">
        Open source AI chatbot built with{' '}
        <a href="https://nextjs.org" className="underline" target="_blank" rel="noopener noreferrer">
          Next.js
        </a>{' '}
        and{' '}
        <a href="https://vercel.com/docs/concepts/functions/edge-functions/vercel-ai-sdk" className="underline" target="_blank" rel="noopener noreferrer">
          Vercel AI SDK
        </a>.
      </span>
    </div>
  </div>
  );
};

export default App;
