import React, { useState } from 'react';
import { MediaRecorder } from 'react-media-recorder';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [mediaBlobUrl, setMediaBlobUrl] = useState(null);

  const handleSend = async () => {
    if (mediaBlobUrl) {
      fetch(mediaBlobUrl)
        .then((res) => res.blob())
        .then((blob) => {
          // Placeholder for sending audio blob to backend
          alert('Audio ready to be sent!');
          // Reset after sending
          setMediaBlobUrl(null);
        });
    }
  };

  return (
    <div className="h-screen flex items-end justify-center bg-gray-900">
      <div className="w-full max-w-3xl">
        <div className="flex flex-col h-screen">
          <div className="flex-1 p-4 overflow-y-auto">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`my-2 p-2 rounded-lg ${message.sender === 'user' ? 'bg-blue-500 text-white self-end' : 'bg-gray-200 text-black self-start'}`}
              >
                {message.text}
              </div>
            ))}
          </div>
          <div className="p-4 m-1 border-t flex rounded-2xl shadow-lg bg-black">
            <MediaRecorder
              audio
              render={({ startRecording, stopRecording, mediaBlobUrl }) => (
                <>
                  <div
                    className="flex-1 p-2 border rounded-xl mr-2 cursor-pointer flex items-center justify-center bg-gray-800 text-white"
                    onClick={() => {
                      if (isRecording) {
                        stopRecording();
                        setIsRecording(false);
                        setMediaBlobUrl(mediaBlobUrl);
                      } else {
                        startRecording();
                        setIsRecording(true);
                      }
                    }}
                  >
                    {isRecording ? 'Stop Recording' : 'Send a message.'}
                  </div>
                  <button
                    className="bg-blue-500 text-white p-2 rounded-lg"
                    onClick={handleSend}
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="w-6 h-6">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                    </svg>
                  </button>
                </>
              )}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
